(function($){$(document).ready(function(){$(document).on("click",".wpmf_vc_select_pdf",function(e){if(typeof frame!=="undefined"){frame.open();return}var frame=wp.media({library:{type:"application/pdf"}});frame.on("select",function(){var attachment=frame.state().get("selection").first().toJSON();$(".pdfembed_url_field").val(attachment.url)});frame.open()});$(document).on("click",".wpmf_vc_select_file",function(e){if(typeof frame!=="undefined"){frame.open();return}var frame=wp.media({library:{type:["application/*","video","audio"]}});frame.on("select",function(){var attachment=frame.state().get("selection").first().toJSON();$(".singlefile_url_field").val(attachment.url)});frame.open()})})})(jQuery);